//id (from supabase id
//username
//datecreated

//other fields
//avatarphoto
//contacts

//just borrow from model_large/chat_user but omit email.
//all fields of User will be used for conducting user searches.

import '../../singletons.dart';

class ChatUser {
  /// Uid generated by Supabase Auth. Only value is ''.
  final String uid;
  /// Name of user.
  final String username;
  /// Email address of user.
  final String signature;
  /// Image of Url, if not provided a default will be used.
  final String avatarUrl;
  /// Date of creation.
  final DateTime dateCreated;
  /// NEW: List of uids as contacts. Starts with null.
  final List<String> contacts;

  /// Constructor.
  ChatUser(
    this.uid,
    this.username,
    this.signature,
    this.avatarUrl,
    this.dateCreated,
    this.contacts
  );

  /// Converts a ChatUser2 object to a Map.
  Map<String, dynamic> toMap() {
    return {
      'uid': uid ?? '',
      'username': username,
      'signature': signature,
      'avatar_url': avatarUrl,
      'date_created': dateCreated.toIso8601String(),
      'contacts': contacts,
    };
  }

  static ChatUser fromMap(Map<String, dynamic> map) {
    return ChatUser(
      map['uid'] as String,
      map['name'] as String,
      map['signature'] as String ?? '', map['avatarUrl'] as String ?? '',
      DateTime.parse(map['dateCreated'] as String),
      List<String>.from(map['contacts'] ?? []),
    );
  }
}